import React from "react";
import { MDBContainer, MDBRow, MDBCol, MDBCard, MDBCardBody, MDBInput, MDBBtn, MDBCardText } from 'mdbreact';
import './css/HomePage.css';
import OC from './oc';
import ipfs from './ipfs';
import axios from 'axios';

class FormPage extends React.Component {

  state = {
    dflag:false,
    dkey: null,
    donorid: '',
    organ: '',
    ipfsHash: '',
    bgroup: '',
    recipientid: '',
    dname: '',
    dcity: '',
    daddress: '',
    demail: '',
    dcontact: '',
    hid: '',
    haddress:'',
    hname:'',
    hcity:'',
    hcontact:'',
    rflag:false,
    rhash: '',
    rorgan: '',
    rbgroup: '',
    rid: '',
    rname: '',
    rcity: '',
    raddress: '',
    rcontact: '',
    remail: ''
  }


  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
    console.log(this.state);
  }

  onSubmit = async () => {
    event.preventDefault();
    console.log("Dkey: " + this.state.dkey);
    console.log("record ke submit ke andar");
    await OC.methods.getdonor(this.state.dkey).call().then((donor) => {
      console.log(donor);
      this.setState({
        donorid: donor[0],
        ipfsHash: donor[1],
        organ: donor[2],
        bgroup: donor[3],
        recipientid: donor[4]

      });
      console.log(this.state);
      ipfs.cat(donor[1], (err, res) => {
        if (err) {
          console.error(err)

        }
        const temp = JSON.parse(res.toString());
        console.log("temp:"+temp);
        var ten = JSON.parse(temp);
        this.setState({
          dname: ten["name"],
          daddress: ten["address"],
          dcity: ten["city"],
